	(@CentaurLongbowId, 1, 0, 2, 0, 150, 600, @ArrowId, 1, 0, 4, null, null, 0, null, 0, ''),
	(@ChimeraMultiattackId, 1, 0, 1, 0, 0, 0, null, 4, 0, 0, null, null, 0, null, 0, 'The chimera makes three attacks: one with its bite, one with its horns, and one with its claws. When its fire breath is available, it can use the breath in place of its bite or horns.'),
	(@ChimeraBiteId, 1, 0, 1, 5, 0, 0, null, 1, 0, 7, null, null, 0, null, 0, ''),
