	INSERT INTO `attributes` (`name`, `description`, `attribute_type_id`, `user_id`, `sid`) VALUES 
	('Lawful Good', 'Lawful good creatures can be counted on to do the right thing as expected by society. Gold dragons, paladins, and most dwarves are lawful good.', @alignmentId, userId, 745),
	('Neutral Good', 'Neutral good folk do the best they can to help others according to their needs. Many celestials, some cloud giants, and most gnomes are neutral good.', @alignmentId, userId, 746),
	('Chaotic Good', 'Chaotic good creatures act as their conscience directs, with little regard for what others expect. Copper dragons, many elves, and unicorns are chaotic good.', @alignmentId, userId, 747),
	('Lawful Neutral', 'Lawful neutral individuals act in accordance with law, tradition, or personal codes. Many monks and some wizards are lawful neutral.', @alignmentId, userId, 748),
	('Neutral', 'Neutral is the alignment of those who prefer to steer clear of moral questions and don’t take sides, doing what seems best at the time. Lizardfolk, most druids, and many humans are neutral.', @alignmentId, userId, 749),
	('Chaotic Neutral ', 'Chaotic neutral creatures follow their whims, holding their personal freedom above all else. Many barbarians and rogues, and some bards, are chaotic neutral.', @alignmentId, userId, 750),
	('Lawful Evil', 'Lawful evil creatures methodically take what they want, within the limits of a code of tradition, loyalty, or order. Devils, blue dragons, and hobgoblins are lawful evil.', @alignmentId, userId, 751),
	('Neutral Evil', 'Neutral evil is the alignment of those who do whatever they can get away with, without compassion or qualms. Many drow, some cloud giants, and goblins are neutral evil.', @alignmentId, userId, 752),
	('Chaotic Evil', 'Chaotic evil creatures act with arbitrary violence, spurred by their greed, hatred, or bloodlust. Demons, red dragons, and orcs are chaotic evil.', @alignmentId, userId, 753);
