	INSERT INTO `items` (`name`, `item_type_id`, `expendable`, `equippable`, `slot`, `container`, `ignore_weight`, `cost`, `cost_unit`, `weight`, `description`, `user_id`, `sid`) VALUES
	('Padded',          @armorId, 0, 1, @bodySlotId, 0, 0, 5,    @gpId, 8,  'Padded armor consists of quilted layers of cloth and batting.', userId, 160),
	('Leather',         @armorId, 0, 1, @bodySlotId, 0, 0, 10,   @gpId, 10, 'The breastplate and shoulder protectors of this armor are made of leather that has been stiffened by being boiled in oil. The rest of the armor is made of softer and more flexible materials.', userId, 161),
	('Studded Leather', @armorId, 0, 1, @bodySlotId, 0, 0, 45,   @gpId, 13, 'Made from tough but flexible leather, studded leather is reinforced with close-set rivets or spikes.', userId, 162),
	('Hide',            @armorId, 0, 1, @bodySlotId, 0, 0, 10,   @gpId, 12, 'This crude armor consists of thick furs and pelts. It is commonly worn by barbarian tribes, evil humanoids, and other folk who lack access to the tools and materials needed to create better armor.', userId, 163),
	('Chain Shirt',     @armorId, 0, 1, @bodySlotId, 0, 0, 50,   @gpId, 20, 'Made of interlocking metal rings, a chain shirt is worn between layers of clothing or leather. This armor offers modest protection to the wearer''s upper body and allows the sound of the rings rubbing against one another to be muffled by outer layers.', userId, 164),
	('Scale Mail',      @armorId, 0, 1, @bodySlotId, 0, 0, 50,   @gpId, 45, 'This armor consists of a coat and leggings (and perhaps a separate skirt) of leather covered with overlapping pieces of metal, much like the scales of a fish. The suit includes gauntlets.', userId, 165),
	('Breastplate',     @armorId, 0, 1, @bodySlotId, 0, 0, 400,  @gpId, 20, 'This armor consists of a fitted metal chest piece worn with supple leather. Although it leaves the legs and arms relatively unprotected, this armor provides good protection for the wearer''s vital organs while leaving the wearer relatively unencumbered.', userId, 166),
	('Half Plate',      @armorId, 0, 1, @bodySlotId, 0, 0, 750,  @gpId, 40, 'Half plate consists of shaped metal plates that cover most of the wearer''s body. It does not include leg protection beyond simple greaves that are attached with leather straps.', userId, 167),
	('Ring Mail',       @armorId, 0, 1, @bodySlotId, 0, 0, 30,   @gpId, 40, 'This armor is leather armor with heavy rings sewn into it. The rings help reinforce the armor against blows from swords and axes. Ring mail is inferior to chain mail, and it''s usually worn only by those who can''t afford better armor.', userId, 168),
	('Chain Mail',      @armorId, 0, 1, @bodySlotId, 0, 0, 75,   @gpId, 55, 'Made of interlocking metal rings, chain mail includes a layer of quilted fabric worn underneath the mail to prevent chafing and to cushion the impact of blows. The suit includes gauntlets.', userId, 169),
	('Splint',          @armorId, 0, 1, @bodySlotId, 0, 0, 200,  @gpId, 60, 'This armor is made of narrow vertical strips of meal riveted to a backing of leather that is worn over cloth padding. Flexible chain mail protects the joints.', userId, 170),
	('Plate',           @armorId, 0, 1, @bodySlotId, 0, 0, 1500, @gpId, 65, 'Plate consists of shaped, interlocking metal plates to cover the entire body. A suit of plate includes gauntlets, heavy leather boots, a visored helmet, and thick layers of padding underneath the armor. Buckles and straps distribute the weight over the body.', userId, 171),
	('Shield',          @armorId, 0, 1, @handSlotId, 0, 0, 10,   @gpId, 6,  'A shield is made from wood or metal and is carried in one hand. You can benefit from only one shield at a time.', userId, 172);
